``` cpp
#include <iostream>
#include <eigen3/Eigen/Dense>

using namespace std;
using namespace Eigen;

int main()
{

    Matrix3d m = Matrix3d::Random();
    m = ( m + Matrix3d::Constant(1.2)) * 50;

